CREATE PROCEDURE SpInsertagTipoTransaccion @empresa int,@actual int,@longitud int,@naturaleza int,@fechaRegistro datetime,@numeracion bit,@referencia bit,@activo bit,@codigo varchar(50),@descripcion varchar(50),@prefijo varchar(50),@modulo varchar(50),@vistaDs varchar(250),@modoAnulacion char(1),@Retorno int output  AS begin tran gTipoTransaccion insert gTipoTransaccion( empresa,actual,longitud,naturaleza,fechaRegistro,numeracion,referencia,activo,codigo,descripcion,prefijo,modulo,vistaDs,modoAnulacion ) select @empresa,@actual,@longitud,@naturaleza,@fechaRegistro,@numeracion,@referencia,@activo,@codigo,@descripcion,@prefijo,@modulo,@vistaDs,@modoAnulacion if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoTransaccion end else begin set @Retorno = 1 rollback tran gTipoTransaccion end