CREATE PROCEDURE SpInsertagTipoTransaccionCampo @empresa int,@tercero bit,@aplicaCliente bit,@aplicaProveedor bit,@aplicaTercero bit,@terceroDefecto bit,@tipoTransaccion varchar(50),@entidad varchar(250),@campo varchar(250),@tipoCampo varchar(50),@Retorno int output  AS begin tran gTipoTransaccionCampo insert gTipoTransaccionCampo( empresa,tercero,aplicaCliente,aplicaProveedor,aplicaTercero,terceroDefecto,tipoTransaccion,entidad,campo,tipoCampo ) select @empresa,@tercero,@aplicaCliente,@aplicaProveedor,@aplicaTercero,@terceroDefecto,@tipoTransaccion,@entidad,@campo,@tipoCampo if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoTransaccionCampo end else begin set @Retorno = 1 rollback tran gTipoTransaccionCampo end