CREATE PROCEDURE SpInsertaiMayorItem @empresa int,@activo bit,@codigo varchar(50),@planes varchar(5),@descripcion varchar(950),@observacion varchar(1550),@Retorno int output  AS begin tran iMayorItem insert iMayorItem( empresa,activo,codigo,planes,descripcion,observacion ) select @empresa,@activo,@codigo,@planes,@descripcion,@observacion if (@@error = 0 ) begin set @Retorno = 0 commit tran iMayorItem end else begin set @Retorno = 1 rollback tran iMayorItem end