CREATE PROCEDURE SpInsertalAnalisis @empresa int,@orden int,@produccion bit,@despacho bit,@informe bit,@control bit,@activo bit,@descuenta bit,@codigo varchar(10),@descripcion varchar(250),@uMedida varchar(50),@Retorno int output  AS begin tran lAnalisis insert lAnalisis( empresa,orden,produccion,despacho,informe,control,activo,descuenta,codigo,descripcion,uMedida ) select @empresa,@orden,@produccion,@despacho,@informe,@control,@activo,@descuenta,@codigo,@descripcion,@uMedida if (@@error = 0 ) begin set @Retorno = 0 commit tran lAnalisis end else begin set @Retorno = 1 rollback tran lAnalisis end