CREATE PROCEDURE SpInsertalRegistroTanque @empresa int,@cantidad int,@tipo varchar(50),@numero varchar(50),@bodega varchar(5),@Retorno int output  AS begin tran lRegistroTanque insert lRegistroTanque( empresa,cantidad,tipo,numero,bodega ) select @empresa,@cantidad,@tipo,@numero,@bodega if (@@error = 0 ) begin set @Retorno = 0 commit tran lRegistroTanque end else begin set @Retorno = 1 rollback tran lRegistroTanque end