CREATE PROCEDURE SpInsertalogProgramacionVehiculoEstado @empresa int,@registro varchar(50),@numeroTransaccion varchar(50),@estado char(10),@Retorno int output  AS begin tran logProgramacionVehiculoEstado insert logProgramacionVehiculoEstado( empresa,registro,numeroTransaccion,estado ) select @empresa,@registro,@numeroTransaccion,@estado if (@@error = 0 ) begin set @Retorno = 0 commit tran logProgramacionVehiculoEstado end else begin set @Retorno = 1 rollback tran logProgramacionVehiculoEstado end