CREATE PROCEDURE [dbo].[SpInsertanCargo] @empresa int,@salarioMaximo money,@activo bit,@codigo varchar(50),@descripcion varchar(550),@CNO varchar(50),@jefeInmediato varchar(50),@observacion varchar(5550),@Retorno int output  AS begin tran nCargo insert nCargo( empresa,salarioMaximo,activo,codigo,descripcion,CNO,jefeInmediato,observacion ) select @empresa,@salarioMaximo,@activo,@codigo,@descripcion,@CNO,@jefeInmediato,@observacion if (@@error = 0 ) begin set @Retorno = 0 commit tran nCargo end else begin set @Retorno = 1 rollback tran nCargo end