CREATE PROCEDURE [dbo].[SpInsertanEntidadSena] @empresa int,@tercero int,@fechaRegistro datetime,@integral bit,@activo bit,@pAporte decimal(18,4),@codigo varchar(50),@descripcion varchar(550),@proveedor varchar(50),@codigoNacional varchar(50),@pais varchar(50),@ciudad varchar(50),@observacion varchar(5550),@usuario varchar(50),@cuenta varchar(50),@Retorno int output  AS begin tran nEntidadSena insert nEntidadSena( empresa,tercero,fechaRegistro,integral,activo,pAporte,codigo,descripcion,proveedor,codigoNacional,pais,ciudad,observacion,usuario,cuenta ) select @empresa,@tercero,@fechaRegistro,@integral,@activo,@pAporte,@codigo,@descripcion,@proveedor,@codigoNacional,@pais,@ciudad,@observacion,@usuario,@cuenta if (@@error = 0 ) begin set @Retorno = 0 commit tran nEntidadSena end else begin set @Retorno = 1 rollback tran nEntidadSena end