create PROCEDURE [dbo].[SpInsertanVacacionesDetalle] @periodoInicial date,@periodoFinal date,@empresa int,@empleado int,@registro int,@signo int,@noDias int,@valorUnitario money,@valorTotal money,@saldo money,@baseSeguridadSocial bit,@baseEmbargos bit,@cantidad decimal,@porcentaje decimal,@concepto varchar(50),@entidad varchar(50),@Retorno int output  AS begin tran nVacacionesDetalle insert nVacacionesDetalle( periodoInicial,periodoFinal,empresa,empleado,registro,signo,noDias,valorUnitario,valorTotal,saldo,baseSeguridadSocial,baseEmbargos,cantidad,porcentaje,concepto,entidad ) select @periodoInicial,@periodoFinal,@empresa,@empleado,@registro,@signo,@noDias,@valorUnitario,@valorTotal,@saldo,@baseSeguridadSocial,@baseEmbargos,@cantidad,@porcentaje,@concepto,@entidad if (@@error = 0 ) begin set @Retorno = 0 commit tran nVacacionesDetalle end else begin set @Retorno = 1 rollback tran nVacacionesDetalle end