CREATE PROCEDURE [dbo].[SpInsertasUsuarioPerfiles] @empresa int,@activo bit,@usuario varchar(50),@perfil varchar(50),@Retorno int output  AS begin tran sUsuarioPerfiles insert sUsuarioPerfiles( empresa,activo,usuario,perfil ) select @empresa,@activo,@usuario,@perfil if (@@error = 0 ) begin set @Retorno = 0 commit tran sUsuarioPerfiles end else begin set @Retorno = 1 rollback tran sUsuarioPerfiles end