create PROCEDURE [dbo].[SpActualizaaLoteCcostoSigo] @empresa int,@lote varchar(50),@mCcostoSigo varchar(50),@aCcostoSigo varchar(50),@Retorno int output  AS begin tran aLoteCcostoSigo update aLoteCcostoSigo set mCcostoSigo = @mCcostoSigo,aCcostoSigo = @aCcostoSigo where empresa = @empresa and lote = @lote if (@@error = 0 ) begin set @Retorno = 0 commit tran aLoteCcostoSigo end else begin set @Retorno = 1 rollback tran aLoteCcostoSigo end