CREATE PROCEDURE SpActualizaaTipoCanal @empresa int,@activo bit,@codigo varchar(10),@descripcion varchar(550),@Retorno int output  AS begin tran aTipoCanal update aTipoCanal set activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran aTipoCanal end else begin set @Retorno = 1 rollback tran aTipoCanal end