CREATE PROCEDURE SpActualizaaTransaccionTercero @empresa int,@año int,@mes int,@registro int,@registroNovedad int,@tercero int,@ejecutado bit,@cantidad decimal,@jornales decimal,@saldo decimal,@tipo varchar(50),@numero varchar(50),@novedad varchar(50),@seccion varchar(50),@lote varchar(50),@zCuadrilla varchar(50),@Retorno int output  AS begin tran aTransaccionTercero update aTransaccionTercero set año = @año,mes = @mes,tercero = @tercero,ejecutado = @ejecutado,cantidad = @cantidad,jornales = @jornales,saldo = @saldo,seccion = @seccion,lote = @lote,zCuadrilla = @zCuadrilla where empresa = @empresa and novedad = @novedad and numero = @numero and registro = @registro and registroNovedad = @registroNovedad and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran aTransaccionTercero end else begin set @Retorno = 1 rollback tran aTransaccionTercero end