CREATE PROCEDURE SpActualizabTipoVehiculo @empresa int,@aplicaRemolque bit,@activo bit,@codigo varchar(50),@descripcion varchar(250),@Retorno int output  AS begin tran bTipoVehiculo update bTipoVehiculo set aplicaRemolque = @aplicaRemolque,activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran bTipoVehiculo end else begin set @Retorno = 1 rollback tran bTipoVehiculo end