CREATE PROCEDURE SpActualizabVehiculo @empresa int,@pesoTara int,@fechaRegistro datetime,@activo bit,@codigo varchar(50),@descripcion varchar(550),@usuario varchar(50),@tipo nchar,@Retorno int output  AS begin tran bVehiculo update bVehiculo set pesoTara = @pesoTara,fechaRegistro = @fechaRegistro,activo = @activo,descripcion = @descripcion,usuario = @usuario where codigo = @codigo and empresa = @empresa and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran bVehiculo end else begin set @Retorno = 1 rollback tran bVehiculo end