CREATE PROCEDURE SpActualizacEstructuraCCosto @empresa int,@nivel int,@inicio int,@tamaño int,@total int,@activo bit,@descripcion varchar(550),@Retorno int output  AS begin tran cEstructuraCCosto update cEstructuraCCosto set inicio = @inicio,tamaño = @tamaño,total = @total,activo = @activo,descripcion = @descripcion where empresa = @empresa and nivel = @nivel if (@@error = 0 ) begin set @Retorno = 0 commit tran cEstructuraCCosto end else begin set @Retorno = 1 rollback tran cEstructuraCCosto end