CREATE PROCEDURE SpActualizacGrupoCCosto @empresa int,@activo bit,@codigo varchar(5),@descripcion varchar(550),@Retorno int output  AS begin tran cGrupoCCosto update cGrupoCCosto set activo = @activo,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cGrupoCCosto end else begin set @Retorno = 1 rollback tran cGrupoCCosto end