CREATE PROCEDURE SpActualizacxcCliente @empresa int,@idTercero int,@fechaRegistro datetime,@activo bit,@codigo varchar(10),@descripcion varchar(550),@contacto varchar(550),@direccion varchar(950),@telefono varchar(50),@email varchar(90),@ciudad char(5),@Retorno int output  AS begin tran cxcCliente update cxcCliente set fechaRegistro = @fechaRegistro,activo = @activo,descripcion = @descripcion,contacto = @contacto,direccion = @direccion,telefono = @telefono,email = @email,ciudad = @ciudad where codigo = @codigo and empresa = @empresa and idTercero = @idTercero if (@@error = 0 ) begin set @Retorno = 0 commit tran cxcCliente end else begin set @Retorno = 1 rollback tran cxcCliente end