CREATE PROCEDURE SpActualizacxpClaseProveedor @empresa int,@descripcion varchar(950),@codigo char(4),@Retorno int output  AS begin tran cxpClaseProveedor update cxpClaseProveedor set descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cxpClaseProveedor end else begin set @Retorno = 1 rollback tran cxpClaseProveedor end