CREATE PROCEDURE SpActualizacxpProveedor @empresa int,@idTercero int,@fechaRegistro datetime,@activo bit,@entradaDirecta bit,@codigo varchar(10),@descripcion varchar(950),@contacto varchar(950),@direccion varchar(950),@telefono varchar(50),@email varchar(250),@clase char(4),@ciudad char(5),@Retorno int output  AS begin tran cxpProveedor update cxpProveedor set fechaRegistro = @fechaRegistro,activo = @activo,entradaDirecta = @entradaDirecta,descripcion = @descripcion,contacto = @contacto,direccion = @direccion,telefono = @telefono,email = @email,clase = @clase,ciudad = @ciudad where codigo = @codigo and empresa = @empresa and idTercero = @idTercero if (@@error = 0 ) begin set @Retorno = 0 commit tran cxpProveedor end else begin set @Retorno = 1 rollback tran cxpProveedor end