CREATE PROCEDURE SpActualizacxpProveedorBanco @empresa int,@proveedor int,@banco varchar(10),@nroCuenta varchar(150),@tipoCuenta char(1),@ciudad char(5),@Retorno int output  AS begin tran cxpProveedorBanco update cxpProveedorBanco set nroCuenta = @nroCuenta,tipoCuenta = @tipoCuenta,ciudad = @ciudad where banco = @banco and empresa = @empresa and proveedor = @proveedor if (@@error = 0 ) begin set @Retorno = 0 commit tran cxpProveedorBanco end else begin set @Retorno = 1 rollback tran cxpProveedorBanco end