create PROCEDURE [dbo].[SpActualizagDepartamento] @empresa int,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran gDepartamento update gDepartamento set descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gDepartamento end else begin set @Retorno = 1 rollback tran gDepartamento end