CREATE PROCEDURE [dbo].[SpActualizagFormaPago] @empresa int,@cheque bit,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran gFormaPago update gFormaPago set cheque = @cheque,descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gFormaPago end else begin set @Retorno = 1 rollback tran gFormaPago end