CREATE PROCEDURE SpActualizagTipoCuentaBancaria @empresa int,@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran gTipoCuentaBancaria update gTipoCuentaBancaria set descripcion = @descripcion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoCuentaBancaria end else begin set @Retorno = 1 rollback tran gTipoCuentaBancaria end