CREATE PROCEDURE SpActualizagTipoDocumento @empresa int,@codigoTD int,@descripcion varchar(250),@codigo char(2),@descripcionCorta char(3),@Retorno int output  AS begin tran gTipoDocumento update gTipoDocumento set codigoTD = @codigoTD,descripcion = @descripcion,descripcionCorta = @descripcionCorta where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoDocumento end else begin set @Retorno = 1 rollback tran gTipoDocumento end