CREATE PROCEDURE SpActualizagTipoTransaccionCampo @empresa int,@tercero bit,@aplicaCliente bit,@aplicaProveedor bit,@aplicaTercero bit,@terceroDefecto bit,@tipoTransaccion varchar(50),@entidad varchar(250),@campo varchar(250),@tipoCampo varchar(50),@Retorno int output  AS begin tran gTipoTransaccionCampo update gTipoTransaccionCampo set tercero = @tercero,aplicaCliente = @aplicaCliente,aplicaProveedor = @aplicaProveedor,aplicaTercero = @aplicaTercero,terceroDefecto = @terceroDefecto,tipoCampo = @tipoCampo where campo = @campo and empresa = @empresa and entidad = @entidad and tipoTransaccion = @tipoTransaccion if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoTransaccionCampo end else begin set @Retorno = 1 rollback tran gTipoTransaccionCampo end