create PROCEDURE [dbo].[SpActualizagTipoTransaccionProducto] @empresa int,@comercializadora bit,@tipo varchar(50),@producto varchar(50),@remision varchar(50),@Retorno int output  AS begin tran gTipoTransaccionProducto update gTipoTransaccionProducto set  producto=@producto where empresa = @empresa and producto = @producto and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoTransaccionProducto end else begin set @Retorno = 1 rollback tran gTipoTransaccionProducto end