CREATE PROCEDURE SpActualizalRegistroAnalisisTanque @empresa int,@porcentaje int,@tipo varchar(50),@numero varchar(50),@tanque varchar(50),@Retorno int output  AS begin tran lRegistroAnalisisTanque update lRegistroAnalisisTanque set porcentaje = @porcentaje where empresa = @empresa and numero = @numero and tanque = @tanque and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran lRegistroAnalisisTanque end else begin set @Retorno = 1 rollback tran lRegistroAnalisisTanque end