CREATE PROCEDURE SpActualizalogCarnetDespacho @empresa int,@codigo varchar(50),@estado char(1),@Retorno int output  AS begin tran logCarnetDespacho update logCarnetDespacho set estado = @estado where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran logCarnetDespacho end else begin set @Retorno = 1 rollback tran logCarnetDespacho end