CREATE PROCEDURE SpActualizalogProgramacionVehiculoEstado @empresa int,@registro varchar(50),@numeroTransaccion varchar(50),@estado char(10),@Retorno int output  AS begin tran logProgramacionVehiculoEstado update logProgramacionVehiculoEstado set numeroTransaccion = @numeroTransaccion,estado = @estado where empresa = @empresa and registro = @registro if (@@error = 0 ) begin set @Retorno = 0 commit tran logProgramacionVehiculoEstado end else begin set @Retorno = 1 rollback tran logProgramacionVehiculoEstado end