CREATE PROCEDURE SpActualizanCcostoNomina @empresa int,@activo bit,@codigo varchar(50),@descripcion varchar(150),@Retorno int output  AS begin tran nCcostoNomina update nCcostoNomina set activo = @activo,descripcion = @descripcion where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nCcostoNomina end else begin set @Retorno = 1 rollback tran nCcostoNomina end