CREATE PROCEDURE [dbo].[SpActualizanDepartamento] @empresa int,@activo bit,@ccosto varchar(50),@codigo varchar(50),@descripcion varchar(550),@Retorno int output  AS begin tran nDepartamento update nDepartamento set activo = @activo,ccosto = @ccosto,descripcion = @descripcion where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nDepartamento end else begin set @Retorno = 1 rollback tran nDepartamento end