create PROCEDURE [dbo].[SpActualizanGrupoConcepto] @empresa int,@activo bit,@descripcion varchar(950),@observacion varchar(1550),@codigo char(5),@Retorno int output  AS begin tran nGrupoConcepto update nGrupoConcepto set activo = @activo,descripcion = @descripcion,observacion = @observacion where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran nGrupoConcepto end else begin set @Retorno = 1 rollback tran nGrupoConcepto end