CREATE PROCEDURE [dbo].[SpActualizanPeriodoDetalle] @fechaInicial date,@fechaFinal date,@fechaCorte date,@empresa int,@año int,@mes int,@noPeriodo int,@diasNomina int,@fechaPago datetime,@fechaRegistro datetime,@cerrado bit,@usuario varchar(50),@tipoNomina varchar(50),@agronomico bit,@Retorno int output  AS begin tran nPeriodoDetalle update nPeriodoDetalle set fechaInicial = @fechaInicial,fechaFinal = @fechaFinal,fechaCorte = @fechaCorte,diasNomina = @diasNomina,fechaPago = @fechaPago,fechaRegistro = @fechaRegistro,cerrado = @cerrado,usuario = @usuario,tipoNomina = @tipoNomina, agronomico=@agronomico where año = @año and empresa = @empresa and mes = @mes and noPeriodo = @noPeriodo if (@@error = 0 ) begin set @Retorno = 0 commit tran nPeriodoDetalle end else begin set @Retorno = 1 rollback tran nPeriodoDetalle end