create PROCEDURE [dbo].[SpActualizanTurnoDepartamento] @empresa int,@activo bit,@turno varchar(50),@departamento varchar(50),@Retorno int output  AS begin tran nTurnoDepartamento update nTurnoDepartamento set activo = @activo where departamento = @departamento and empresa = @empresa and turno = @turno if (@@error = 0 ) begin set @Retorno = 0 commit tran nTurnoDepartamento end else begin set @Retorno = 1 rollback tran nTurnoDepartamento end