CREATE PROCEDURE SpActualizapFrutaEstimadaTmp @fecha date,@empresa int,@pesoNeto int,@Retorno int output  AS begin tran pFrutaEstimadaTmp update pFrutaEstimadaTmp set pesoNeto = @pesoNeto where empresa = @empresa and fecha = @fecha if (@@error = 0 ) begin set @Retorno = 0 commit tran pFrutaEstimadaTmp end else begin set @Retorno = 1 rollback tran pFrutaEstimadaTmp end