CREATE PROCEDURE SpActualizasPerfilPermisos @activo bit,@perfil varchar(50),@sitio varchar(150),@menu varchar(150),@operacion varchar(50),@Retorno int output  AS begin tran sPerfilPermisos update sPerfilPermisos set activo = @activo where menu = @menu and operacion = @operacion and perfil = @perfil and sitio = @sitio if (@@error = 0 ) begin set @Retorno = 0 commit tran sPerfilPermisos end else begin set @Retorno = 1 rollback tran sPerfilPermisos end