CREATE PROCEDURE SpActualizasUsuarios @empleado int,@fechaRegistro datetime,@activo bit,@clave varbinary,@usuario varchar(50),@descripcion varchar(250),@email varchar(250),@Retorno int output  AS begin tran sUsuarios update sUsuarios set empleado = @empleado,fechaRegistro = @fechaRegistro,activo = @activo,clave = @clave,descripcion = @descripcion,email = @email where usuario = @usuario if (@@error = 0 ) begin set @Retorno = 0 commit tran sUsuarios end else begin set @Retorno = 1 rollback tran sUsuarios end