CREATE PROCEDURE SpActualizasysEntidadMetodos @entidad varchar(250),@dBase varchar(250),@metodoGet varchar(250),@metodoInsert varchar(250),@metodoUpdate varchar(250),@metodoDelete varchar(250),@metodoGetKey varchar(250),@Retorno int output  AS begin tran sysEntidadMetodos update sysEntidadMetodos set metodoGet = @metodoGet,metodoInsert = @metodoInsert,metodoUpdate = @metodoUpdate,metodoDelete = @metodoDelete,metodoGetKey = @metodoGetKey where dBase = @dBase and entidad = @entidad if (@@error = 0 ) begin set @Retorno = 0 commit tran sysEntidadMetodos end else begin set @Retorno = 1 rollback tran sysEntidadMetodos end