CREATE PROCEDURE [dbo].[SpDeleteaNovedadLotePrecio] @empresa int,@año varchar(50),@Retorno int output  AS begin tran aNovedadLotePrecio delete aNovedadLotePrecio where año = @año and empresa = @empresa 
