CREATE PROCEDURE SpDeletecConceptoIR @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran cConceptoIR delete cConceptoIR where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran cConceptoIR end else begin set @Retorno = 1 rollback tran cConceptoIR end