CREATE PROCEDURE SpDeletecxcClienteClaseIR @empresa int,@tercero int,@clase int,@cliente varchar(10),@Retorno int output  AS begin tran cxcClienteClaseIR delete cxcClienteClaseIR where clase = @clase and cliente = @cliente and empresa = @empresa and tercero = @tercero if (@@error = 0 ) begin set @Retorno = 0 commit tran cxcClienteClaseIR end else begin set @Retorno = 1 rollback tran cxcClienteClaseIR end