CREATE PROCEDURE SpDeletefTipoMercado @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran fTipoMercado delete fTipoMercado where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran fTipoMercado end else begin set @Retorno = 1 rollback tran fTipoMercado end