create PROCEDURE [dbo].[SpDeletegTipoEmbargo] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran gTipoEmbargo delete gTipoEmbargo where codigo = @codigo and empresa = @empresa if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoEmbargo end else begin set @Retorno = 1 rollback tran gTipoEmbargo end