CREATE PROCEDURE SpDeletegTipoTransaccionConfig @empresa int,@tipoTransaccion varchar(50),@Retorno int output  AS begin tran gTipoTransaccionConfig delete gTipoTransaccionConfig where empresa = @empresa and tipoTransaccion = @tipoTransaccion if (@@error = 0 ) begin set @Retorno = 0 commit tran gTipoTransaccionConfig end else begin set @Retorno = 1 rollback tran gTipoTransaccionConfig end