CREATE PROCEDURE SpDeleteiBodegaTipoTransaccion @empresa int,@tipo varchar(50),@bodega varchar(5),@Retorno int output  AS begin tran iBodegaTipoTransaccion delete iBodegaTipoTransaccion where bodega = @bodega and empresa = @empresa and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran iBodegaTipoTransaccion end else begin set @Retorno = 1 rollback tran iBodegaTipoTransaccion end