CREATE PROCEDURE SpDeletelogProgramacionGeneral @empresa int,@programacion varchar(50),@Retorno int output  AS begin tran logProgramacionGeneral delete logProgramacionGeneral where empresa = @empresa and programacion = @programacion if (@@error = 0 ) begin set @Retorno = 0 commit tran logProgramacionGeneral end else begin set @Retorno = 1 rollback tran logProgramacionGeneral end