CREATE PROCEDURE SpDeletelogProgramacionVehiculo @empresa int,@numero varchar(50),@tipo varchar(50),@Retorno int output  AS begin tran logProgramacionVehiculo delete logProgramacionVehiculo where empresa = @empresa and numero = @numero and tipo = @tipo if (@@error = 0 ) begin set @Retorno = 0 commit tran logProgramacionVehiculo end else begin set @Retorno = 1 rollback tran logProgramacionVehiculo end