CREATE PROCEDURE [dbo].[SpDeletenEntidadSena] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nEntidadSena delete nEntidadSena where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nEntidadSena end else begin set @Retorno = 1 rollback tran nEntidadSena end