CREATE PROCEDURE [dbo].[SpDeletenMotivoRetiro] @empresa int,@codigo varchar(50),@Retorno int output  AS begin tran nMotivoRetiro delete nMotivoRetiro where empresa = @empresa and codigo = @codigo if (@@error = 0 ) begin set @Retorno = 0 commit tran nMotivoRetiro end else begin set @Retorno = 1 rollback tran nMotivoRetiro end